function divfor(i){div='<div class="progress-query"><div class="content-item"><div class="con-item-left">预留电话</div><div class="con-item-right phone-num">'+i.phone+'</div></div><div class="content-item">    <div class="con-item-left">卡片状态</div>    <div class="con-item-right card-status">'+i.cardStatus+'</div></div><div class="content-item">    <div class="con-item-left">是否邮寄</div>    <div class="con-item-right need-send">'+i.bNeedSend+'</div></div><div class="content-item">    <div class="con-item-left">邮寄地址</div>    <div class="con-item-right need-send-insert">'+i.location+'</div></div><div class="content-item">    <div class="con-item-left">挂失时间</div>    <div class="con-item-right card-time">'+i.lockTime+"</div></div></div>",$(".container").append(div)}$(function(){$.ajax({url:CONFIG.loginByZst+"ZST/oldCard/getStatus",type:"POST",data:{user:window.localStorage.user},dataType:"json",success:function(i){var e=i.data;if(e&&e.length>0)for(var d=0,t=e.length;d<t;d++)1==e[d].bNeedSend?e[d].bNeedSend="是":e[d].bNeedSend="否",divfor(e[d]);else $(".container").append('<div class="no-record">您没有挂失补办记录</div>')}})});